import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [crimeData, setCrimeData] = useState();

  console.log(fetchCrimeData("e181el"), "crime data");

  async function fetchCrimeData(postcode) {
    const apiKey = "YOUR_API_KEY";
    const apiUrl = `https://data.police.uk/api/crimes-street/all-crime?poly=${postcode}&date=2022-09`;

    try {
      const response = await fetch(apiUrl, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${apiKey}`,
        },
      });
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Error fetching data: ", error);
      return null;
    }
  }

  useEffect(() => {
    fetchCrimeData("e181el").then((response) => {
      setCrimeData(response.results);
    });
  }, []);

  return <div>Home</div>;
};

export default Home;
